import * as React from "react"
import { addPropertyControls, ControlType, motion } from "framer"
import * as Icons from "./assets/icons"
import { defaultEvents } from "./lib/constants"
import { NullState, useIconSelection } from "./lib/iconUtils"

/*
 ** ICON
 */

export interface IconProps {
    width: number
    height: number
    color: string
    selectByList: boolean
    iconSearch: string
    iconSelection: string
    onTap?: any
    onClick?: any
    onMouseEnter?: any
    onMouseLeave?: any
    onMouseDown?: any
    onMouseUp?: any
}

export function Icon(props: IconProps) {
    const {
        width,
        height,
        color,
        selectByList,
        iconSearch,
        iconSelection,
        onClick,
        onMouseDown,
        onMouseUp,
        onMouseEnter,
        onMouseLeave,
    } = props

    const Icon = useIconSelection(Icons, selectByList, iconSearch, iconSelection)

    return (
        <motion.div
            style={{ display: "contents" }}
            {...{ onClick, onMouseEnter, onMouseLeave, onMouseDown, onMouseUp }}
        >
            {Icon ? <Icon icon={name} width={width} height={height} color={color} /> : <NullState />}
        </motion.div>
    )
}
Icon.defaultProps = {
    width: 35,
    height: 35,
    iconSelection: "Framer",
    iconSearch: "Framer",
    color: "#09F",
    selection: true,
}

addPropertyControls(Icon, {
    color: { type: ControlType.Color, title: "Color" },
    selectByList: {
        type: ControlType.Boolean,
        title: "Select",
        enabledTitle: "List",
        disabledTitle: "Search",
    },
    iconSelection: {
        type: ControlType.Enum,
        options: Object.keys(Icons),
        defaultValue: Icon.defaultProps.iconSelection,
        title: "Name",
        hidden: ({ selectByList }) => !selectByList,
    },
    iconSearch: {
        type: ControlType.String,
        title: "Name",
        placeholder: "Menu, Wifi, Boxâ€¦",
        hidden: ({ selectByList }) => selectByList,
    },
    ...defaultEvents,
})
