import * as React from "react"

const letters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz"
const l = () => letters[Math.floor(Math.random() * letters.length)]
const randomID = () => l() + l() + l() + l() + l() + l() + l() + l() + l()

function createUniqueClass(suffix: string): string {
    return `framer-${suffix}-${randomID()}`
}

export function useUniqueClassName(suffix: string, deps: any[]) {
    const [className, setClassName] = React.useState(createUniqueClass(suffix))
    React.useEffect(() => {
        setClassName(createUniqueClass(suffix))
    }, [suffix, ...deps])

    return className
}
