import * as React from "react"
import { addPropertyControls, ControlType } from "framer"

export function Map({ lat, lon, zoom }) {
    const style = {
        height: "100%",
        width: "100%",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        overflow: "hidden",
        border: 0,
    }

    return (
        <iframe
            style={style}
            src={`http://maps.google.com/maps?q=${lat},${lon}&z=${zoom}&output=embed`}
        />
    )
}

Map.defaultProps = {
    lat: -34.903667,
    lon: -56.1701647,
    zoom: 15,
}

addPropertyControls(Map, {
    lat: {
        type: ControlType.Number,
        step: 0.000001,
        min: -90,
        max: 90,
        title: "Latitude",
    },
    lon: {
        type: ControlType.Number,
        step: 0.000001,
        min: -180,
        max: 180,
        title: "Longitude",
    },
    zoom: { type: ControlType.Number, step: 1, min: 0, max: 25, title: "Zoom" },
})
